def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    """Improved version of `priority_v1`."""
    priorities = np.zeros_like(bins)

    for i in range(len(bins)):
        if bins[i] >= item:
            priorities[i] = 0
        else:
            remaining_capacity = bins[i] - item
            priority = 0

            while item > remaining_capacity:
                if item > bins[i] * 0.75:
                    priority += 3
                elif item > bins[i] * 0.5:
                    priority += 2
                elif item > bins[i] * 0.25:
                    priority += 1
                else:
                    priority += 0

                item -= remaining_capacity
                remaining_capacity = min(2 * remaining_capacity, item)

            priority += item / bins[i]

            if priority > 3:
                priorities[i] = 3
            elif priority > 2:
                priorities[i] = 2
            elif priority > 1:
                priorities[i] = 1
            else:
                priorities[i] = priority

    return priorities
