def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    """Improved version of `priority_v1`."""

    priorities = []
    for bin_capacity in bins:
        priority = 0

        if bin_capacity >= item:
            priority = 1
        elif bin_capacity > 0:
            remaining_space = bin_capacity - item
            if remaining_space > 0:
                priority += 0.5 * (remaining_space / bin_capacity)
            else:
                priority -= 0.5 * (item / bin_capacity)
            priority += 0.5 * (bin_capacity / np.sum(bins))
            if item > bin_capacity / 2:
                priority -= 0.25
            else:
                priority += 0.25
            for i in range(1, int(bin_capacity/2)+1):
                priority += 0.1 * (i / (bin_capacity/2))

        else:
            priority -= 0.25

        priorities.append(priority)

    return np.array(priorities)
